INSERT INTO `ef_posts` VALUES('63', '1', '2015-03-17 21:48:46', '2015-03-17 21:48:46', 'Jordan Morgan-Barnes is a marketing specialist and project manager with experience developing and implementing research methodologies both online and in-store. As a Director of the Eye Faster team, Jordan leads the team’s marketing efforts, is no stranger to in-store fielding, and assists with analytics and reporting. Jordan holds a BA in Journalism from Michigan State University. In his spare time, he loves to cook, read comic books, and go on urban adventures near his home in Chicago.', 'Jordan Morgan-Barnes', '', 'publish', 'closed', 'closed', '', 'jordan-morgan-barnes', '', '', '2015-03-17 21:48:46', '2015-03-17 21:48:46', '', '0', 'http://localhost:8888/?post_type=team_members&#038;p=63', '0', 'team_members', '', '0');
