INSERT INTO `ef_posts` VALUES('250', '1', '2015-04-08 22:09:07', '2015-04-08 22:09:07', 'Kirk Hendrickson developed his expertise in eye tracking and shopper research while leading worldwide field operations for EmSense Corporation and product management for MarketTools, Inc. Kirk holds a patent for conducting surveys on mobile phones and was twice a finalist for the AMA/MR Explor Awards. Kirk holds an MBA from the Amos Tuck School of Business Administration, Dartmouth College, and a BS and MS in Mechanical Engineering from Stanford University.', 'Kirk Hendrickson', '', 'inherit', 'open', 'open', '', '56-revision-v1', '', '', '2015-04-08 22:09:07', '2015-04-08 22:09:07', '', '56', 'http://ef.zendentest.com/56-revision-v1/', '0', 'revision', '', '0');
